background.func:
__ht__function(data, view) {
 let d=data.a('node.state'),r='#00FF00';if(d<=0) r='#00FF00';
if(d==100) r='#FF0000'
if(d>0&&d<100) r='#FFFFFF'
 return r;
}

rotation.func:
__ht__function(data, view){
let dir=data.a('node.direction'),res=0;
if(dir === 'up')res=0;
if(dir === 'right')res=90;
if(dir === 'down')res=180;
if(dir === 'left')res=270;
return res/180*Math.PI;
}

